import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';

const ADD_PROMPT = gql`
    mutation AddPrompt($chatId: String!, $prompt: String!, $response: String!, $chatTitle: String!) {
        addPrompt(chatId: $chatId, prompt: $prompt, response: $response, chatTitle: $chatTitle) {
            chatId
            prompt
            response
            chatTitle
        }
    }
`;

function PromptForm() {
    const [formState, setFormState] = useState({
        chatId: '',
        prompt: '',
        response: '',
        chatTitle: ''
    });

    const [addPrompt] = useMutation(ADD_PROMPT);

    const handleSubmit = async (event) => {
        event.preventDefault();
        await addPrompt({ variables: formState });
        setFormState({ chatId: '', prompt: '', response: '', chatTitle: '' });
    };

    return (
        <form onSubmit={handleSubmit} className="mb-4">
            <input type="text" placeholder="Chat ID" value={formState.chatId} onChange={(e) => setFormState({ ...formState, chatId: e.target.value })} required />
            <input type="text" placeholder="Prompt" value={formState.prompt} onChange={(e) => setFormState({ ...formState, prompt: e.target.value })} required />
            <input type="text" placeholder="Response" value={formState.response} onChange={(e) => setFormState({ ...formState, response: e.target.value })} required />
            <input type="text" placeholder="Chat Title" value={formState.chatTitle} onChange={(e) => setFormState({ ...formState, chatTitle: e.target.value })} required />
            <button type="submit">Add Prompt</button>
        </form>
    );
}

export default PromptForm;
